import React, { useContext, useState } from 'react';
import { Divider, Popover, Typography, Table ,  Button, Menu, Dropdown} from 'antd';
import Slider from "react-slick";
import { Pie } from 'react-chartjs-2';
import * as Constants from 'constants/constants';
import HistoryContext from 'contexts/HistoryContext';
import { message } from 'antd';
import {DownOutlined, CalendarOutlined} from '@ant-design/icons';
import MapScreen from 'components/map/MapScreen';

const { Paragraph } = Typography;
//export const [selectedElection, setSelectedElection] = useState(Constants.ELECTIONS[0]);

export default function Election(props) {
    const handleAddHistory = useContext(HistoryContext).handleAddHistory;
    const [rerender, setRerender] = useState(false);
    const [data, setData] = useState(props.data);
    const [selectedElection, setSelectedElection] = useState(Constants.ELECTIONS[0]);

    //message.info(data);
    const columns = [
        {
            title: 'Party',
            dataIndex: 'party',
            key: 'party'
        },
        {
            title: 'Votes',
            dataIndex: 'votes',
            key: 'votes',
            render: (votes, record, index) => (
                <Paragraph editable={{ onChange: value => onChange(value, index)}}>{votes+""}</Paragraph>
            ),
            width: 100,
        },
        {
            title: 'Percent',
            dataIndex: 'percent',
            key: 'percent',
        }
    ];

    // const sum = props.data.reduce((a, b) => a + b);
    // const data = props.data.map((item, index) => {
    //     return {
    //         key: index,
    //         party: Constants.PARTIES[index],
    //         votes: item,
    //         percent: (item/sum*100).toFixed(1)
    //     }
    // });

    function getData() {
        let displayData = [];
        let sum = 0;
        for(let i in data) {
            displayData.push(data[i]);
            message.info(i+"->"+data[i])
            //sum += data[i];
        }
        // displayData = displayData.map((item, index) => {
        //     return {
        //         key: index,
        //         party: Constants.PARTIES[index],
        //         votes: item,
        //         percent: (item/sum*100).toFixed(1)
        //     }
        // });
        return [];
    }

    function onChange(value, index) {
        props.data[index] = Number(value);
        handleAddHistory(`Changed ${props.name}'s ${Constants.PARTIES[index]} votes to ${value}`);
        setRerender(!rerender);
    }

    return (
        <React.Fragment>
            <Divider />
            <h1 className="chartHeader">Election Data</h1>
            <Dropdown
                overlay={
                    <Menu onClick={({ key }) => setSelectedElection(key)}>
                        {
                            Constants.ELECTIONS.map((election) => {
                                return (
                                    <Menu.Item key={election}>
                                        <CalendarOutlined /> {election}
                                    </Menu.Item>
                                );
                            }
                            )
                        }
                    </Menu>
                }
            >
                <div className="electionDiv">
                    <Button
                        type="primary"
                        //icon={<CalendarOutlined />}
                        shape="round"
                        style={{borderRadius: '5px', height: '24.5px', lineHeight: '1em', paddingLeft: '10px', paddingRight: '7px'}}
                    >
                        {selectedElection} <DownOutlined />
                    </Button>
                </div>
            </Dropdown>
            <Popover
                placement="topRight" 
                title={"Source of Data"}
                content={<p>Harvard Election Data Archive</p>}
            >
                <p className="sourceText" style={{marginTop: '-23px'}}><span className="sourceBg">Source of Data</span></p>
            </Popover>
            <br />
            <Slider 
                arrows={false}
                dots={true}
                infinite={true}
                speed={500}
                slidesToShow={1}
                slidesToScroll={1}
            >
                <Pie 
                    data={{
                        labels: Constants.PARTIES,
                        datasets: [{
                            data: getData().map(item => item.votes),
                            backgroundColor: [
                                '#36A2EB',
                                '#FF6384',
                                '#B2EB70',
                                '#FFCE56',
                    
                            ],
                            hoverBackgroundColor: [
                                '#36A2EB',
                                '#FF6384',
                                '#B2EB70',
                                '#FFCE56',
                            ]
                        }]
                    }
                    }
                    borderWidth = {100}
                />
                <Table
                    columns={columns}
                    dataSource={getData()}
                    pagination={false}
                    size={'small'}
                />
            </Slider>
        </React.Fragment>
    );
}
